import "./Editor.css";
import React, { Component } from 'react';
import Output from "./Output";

class Editor extends Component{
    constructor(props){
        super(props);

        this.state = {
            firstName: "First",
            lastName: "Last",
            phone: "123",
            mail: "example@mail.com",
            title: "Sofware Developer",
            address: "Earth",
            brand: "Brand",
            brandLogo: "",
            slogan: "Amazing Slogan Placeholder",
        }
        this.changeFirstName = this.changeFirstName.bind(this);
        this.changeLastName = this.changeLastName.bind(this);
        this.changePhone = this.changePhone.bind(this);
        this.changeMail = this.changeMail.bind(this);
        this.changeTitle = this.changeTitle.bind(this);
        this.changeAddress = this.changeAddress.bind(this);
        this.changeBrand = this.changeBrand.bind(this);
        this.changeBrandLogo = this.changeBrandLogo.bind(this);
        this.changeSlogan = this.changeSlogan.bind(this);
    }
    changeFirstName(){
        this.setState({
            firstName: document.getElementById("fnInput").value,
        })
    }
    changeLastName(){
        this.setState({
            lastName: document.getElementById("lnInput").value,
        })
    }
    changePhone(){
        this.setState({
            phone: document.getElementById("phInput").value,
        })
    }
    changeMail(){
        this.setState({
            mail: document.getElementById("mailInput").value,
        })
    }
    changeTitle(){
        this.setState({
            title: document.getElementById("titleInput").value,
        })
    }
    changeAddress(){
        this.setState({
            address: document.getElementById("addrInput").value,
        })
    }
    changeBrand(){
        this.setState({
            brand: document.getElementById("brInput").value,
        })
    }
    changeBrandLogo(event){
        if (event.target.files && event.target.files[0]) {
            let reader = new FileReader();
            reader.onload = (e) => {
              this.setState({brandLogo: e.target.result});
            };
            reader.readAsDataURL(event.target.files[0]);
          }
    }
    changeSlogan(){
        this.setState({
            slogan: document.getElementById("sloInput").value,
        })
    }
    render(){
        return (
            <div className="test">
                <div style={{width: "50%"}}>
                    <h1>Editor</h1>
                    <div id="personal">
                        <h2>Personal Information</h2>

                        <input className="input" id="fnInput" placeholder="First Name" onChange={this.changeFirstName}/>
                        <input className="input" id="lnInput" placeholder="Last Name" onChange={this.changeLastName}/>
                        <input className="input" id="titleInput" placeholder="Work Profile" onChange={this.changeTitle} />
                        <input className="input" id="phInput" placeholder="Phone" onChange={this.changePhone} />
                        <input className="input" id="mailInput" placeholder="Mail" onChange={this.changeMail} />
                        <input className="input" id="addrInput" placeholder="Physical Address" onChange={this.changeAddress} />
                        <input className="input" id="brInput" placeholder="Brand Name" onChange={this.changeBrand} />
                        <input className="input" id="sloInput" placeholder="Slogan" onChange={this.changeSlogan} />
                        <input type="file" className="input" id="brLogo" placeholder="Brand Logo Address" onChange={this.changeBrandLogo} />


                    </div>
                    <div id="backside">


                    </div>

                </div>
                <Output data={this.state}/>
            </div>
        )
    }
}

export default Editor;
