import "./Editor.css";
import React, { Component } from 'react';
import Output from "./Output";

class Editor extends Component{
    constructor(props){
        super(props);

        this.state = {
            firstName: "First",
            lastName: "Last",
            phone: "123",
        }
        this.changeFirstName = this.changeFirstName.bind(this);
        this.changeLastName = this.changeLastName.bind(this);
    }
    changeFirstName(){
        this.setState({
            firstName: document.getElementById("fnInput").value,
        })
    }
    changeLastName(){
        this.setState({
            lastName: document.getElementById("lnInput").value,
        })
    }
    render(){
        return (
            <div className="test">
                <div style={{width: "50%"}}>
                    <h1>Editor</h1>
                    <input id="fnInput" placeholder="First Name" onChange={this.changeFirstName}/>
                    <input id="lnInput" placeholder="Last Name" onChange={this.changeLastName}/>
                    <input id="phone" placeholder="Phone" onChange={this.changePhone} />
                </div>
                <Output data={this.state}/>
            </div>
        )
    }
}

export default Editor;
