import "./Editor.css";
import React, { Component } from 'react';
import Output from "./Output";

class Editor extends Component{
    constructor(props){
        super(props);

        this.state = {
            firstName: "Dwight",
            lastName: "Schrute",
            phone: "(717)-555-0177",
            mail: "schrute@wuphf.com",
            title: "Regional Manager",
            address: "Chandler Valley Centre Studios, Panorama City, Cali.",
            brand: "Dunder Mifflin",
            brandLogo: "",
            linked: "dwightSchrute",
            github: "proDwight"
        }
        this.changeFirstName = this.changeFirstName.bind(this);
        this.changeLastName = this.changeLastName.bind(this);
        this.changePhone = this.changePhone.bind(this);
        this.changeMail = this.changeMail.bind(this);
        this.changeTitle = this.changeTitle.bind(this);
        this.changeAddress = this.changeAddress.bind(this);
        this.changeBrand = this.changeBrand.bind(this);
        this.changeBrandLogo = this.changeBrandLogo.bind(this);
        this.changeLinkedIn = this.changeLinkedIn.bind(this);
        this.changeGithub = this.changeGithub.bind(this);
    }
    changeFirstName(){
        this.setState({
            firstName: document.getElementById("fnInput").value,
        })
    }
    changeLastName(){
        this.setState({
            lastName: document.getElementById("lnInput").value,
        })
    }
    changePhone(){
        this.setState({
            phone: document.getElementById("phInput").value,
        })
    }
    changeMail(){
        this.setState({
            mail: document.getElementById("mailInput").value,
        })
    }
    changeTitle(){
        this.setState({
            title: document.getElementById("titleInput").value,
        })
    }
    changeAddress(){
        this.setState({
            address: document.getElementById("addrInput").value,
        })
    }
    changeBrand(){
        this.setState({
            brand: document.getElementById("brInput").value,
        })
    }
    changeBrandLogo(event){
        console.log(event);
        if (event.target.files && event.target.files[0]) {
            this.setState({
                brandLogo: URL.createObjectURL(event.target.files[0])
            });
        }
    }
    changeLinkedIn(){
        this.setState({
            linked: document.getElementById("liInput").value,
        });
    }
    changeGithub(){
        this.setState({
            github: document.getElementById("gitInput").value,
        })
    }
    render(){
        return (
            <div className="test">
                <div id="form">
                    <h1>Editor</h1>
                    <div id="personal">
                        <h2>Personal Information</h2>

                        <input title="First name" className="input" id="fnInput" placeholder="First Name" onChange={this.changeFirstName}/>
                        <input title="Last name" className="input" id="lnInput" placeholder="Last Name" onChange={this.changeLastName}/>
                        <input title="Designation" className="input" id="titleInput" placeholder="Work Profile" onChange={this.changeTitle} />
                        <input title="Company" className="input" id="brInput" placeholder="Brand/Company Name" onChange={this.changeBrand} />
                        <input title="Phone number" className="input" id="phInput" placeholder="Phone" onChange={this.changePhone} />
                        <input title="Email address" className="input" id="mailInput" placeholder="Mail" onChange={this.changeMail} />
                        <span title="Brand Logo" className="input">Select a Brand Logo: <input type="file" className="filetype" id="brLogo" placeholder="Brand Logo Address" onChange={this.changeBrandLogo} /></span>
                        <input title="LinkedIn address" className="input" id="liInput" placeholder="LinkedIn" onChange={this.changeLinkedIn} />
                        <input title="Github" className="input" id="gitInput" placeholder="Github" onChange={this.changeGithub} />


                        <input className="input" id="addrInput" placeholder="Physical Address" onChange={this.changeAddress} />
                    </div>
                    <div id="backside">


                    </div>

                </div>
                <Output data={this.state}/>
            </div>
        )
    }
}

export default Editor;
